};

// Cancel booking
const cancelBooking = async (req, res) => {
  try {
    const { id } = req.params;
    const { reason } = req.body;

    const booking = await Booking.findOne({
      _id: id,
      customer: req.user.id
    });

    if (!booking) {
      return res.status(404).json({ message: 'Booking not found' });
    }

    if (booking.status === 'cancelled') {
      return res.status(400).json({ message: 'Booking already cancelled' });
    }

    // Check if cancellation is allowed (e.g., at least 24 hours before start date)
    const now = new Date();
    const startDate = new Date(booking.bookingDetails.startDate);
    const hoursDifference = (startDate - now) / (1000 * 60 * 60);

    if (hoursDifference < 24) {
      return res.status(400).json({ 
        message: 'Cancellation not allowed less than 24 hours before start date' 
      });
    }

    booking.status = 'cancelled';
    booking.notes.push({
      content: `Booking cancelled by customer. Reason: ${reason}`,
      author: req.user.id
    });

    await booking.save();

    // Update tour availability
    const tour = await Tour.findById(booking.tour);
    const availability = tour.availability.find(slot => {
      const bookingDate = booking.bookingDetails.startDate;
      return bookingDate >= slot.startDate && bookingDate <= slot.endDate;
    });

    if (availability) {
      availability.bookedSpots -= booking.bookingDetails.totalTravelers;
      await tour.save();
    }

    res.json({
      message: 'Booking cancelled successfully',
      booking
    });
  } catch (error) {
    console.error('Cancel booking error:', error);
    res.status(500).json({ message: 'Server error cancelling booking' });
  }
};

module.exports = {
  createBooking,
  getUserBookings,
  getBooking,
  cancelBooking
};

const stripe = require('stripe')(process.env.STRIPE_SECRET_KEY);
const Booking = require('../models/Booking');

// Create Stripe payment intent
const createPaymentIntent = async (req, res) => {
  try {
    const { bookingId } = req.body;

    const booking = await Booking.findOne({
      _id: bookingId,
      customer: req.user.id
    }).populate('tour');

    if (!booking) {
      return res.status(404).json({ message: 'Booking not found' });
    }

    if (booking.payment.status === 'paid') {
      return res.status(400).json({ message: 'Booking already paid' });
    }

    // Create payment intent with Stripe
    const paymentIntent = await stripe.paymentIntents.create({
      amount: Math.round(booking.pricing.totalAmount * 100), // Convert to cents
      currency: booking.pricing.currency.toLowerCase(),
      metadata: {
        bookingId: booking._id.toString(),
        tourTitle: booking.tour.title,
        customerEmail: req.user.email
      }
    });

    res.json({
      clientSecret: paymentIntent.client_secret,
      amount: booking.pricing.totalAmount,
      currency: booking.pricing.currency
    });
  } catch (error) {
    console.error('Payment intent error:', error);
    res.status(500).json({ message: 'Error creating payment intent' });
  }
};

// Confirm payment
const confirmPayment = async (req, res) => {
  try {
    const { bookingId, paymentIntentId, paymentMethod } = req.body;

    const booking = await Booking.findOne({
      _id: bookingId,
      customer: req.user.id
    });

    if (!booking) {
      return res.status(404).json({ message: 'Booking not found' });
    }

    // Verify payment with Stripe
    const paymentIntent = await stripe.paymentIntents.retrieve(paymentIntentId);

    if (paymentIntent.status === 'succeeded') {
      booking.payment.status = 'paid';
      booking.payment.method = paymentMethod;
      booking.payment.transactionId = paymentIntentId;
      booking.payment.paidAmount = booking.pricing.totalAmount;
      booking.payment.paymentDate = new Date();
      booking.status = 'confirmed';

      await booking.save();

      res.json({
        message: 'Payment confirmed successfully',
        booking
      });
    } else {
      res.status(400).json({ message: 'Payment not successful' });
    }
  } catch (error) {
    console.error('Payment confirmation error:', error);
    res.status(500).json({ message: 'Error confirming payment' });
  }
};

// Handle VNPay payment (Vietnamese payment gateway)
const createVNPayPayment = async (req, res) => {
  try {
    const { bookingId } = req.body;

    const booking = await Booking.findOne({
      _id: bookingId,
      customer: req.user.id
    });

    if (!booking) {
      return res.status(404).json({ message: 'Booking not found' });
    }

    // VNPay integration would go here
    // This is a simplified version - you'll need to implement the actual VNPay API
    const vnpayData = {
      vnp_Amount: booking.pricing.totalAmount * 100,
      vnp_Command: 'pay',
      vnp_CreateDate: new Date().toISOString().replace(/[-T:\.Z]/g, ''),
      vnp_CurrCode: 'VND',
      vnp_IpAddr: req.ip,
      vnp_Locale: 'vn',
      vnp_OrderInfo: `Payment for booking ${booking.bookingNumber}`,
      vnp_OrderType: 'other',
      vnp_ReturnUrl: `${process.env.CLIENT_URL}/payment/vnpay/return`,
      vnp_TmnCode: process.env.VNPAY_TMN_CODE,
      vnp_TxnRef: booking.bookingNumber,
      vnp_Version: '2.1.0'
    };

    // Generate VNPay URL (simplified)
    const vnpayUrl = `https://sandbox.vnpayment.vn/paymentv2/vpcpay.html`;

    res.json({
      paymentUrl: vnpayUrl,
      vnpayData
    });
  } catch (error) {
    console.error('VNPay payment error:', error);
    res.status(500).json({ message: 'Error creating VNPay payment' });
  }
};

// Handle payment webhook (for automatic payment confirmation)
const handleWebhook = async (req, res) => {
  const sig = req.headers['stripe-signature'];

  try {
    const event = stripe.webhooks.constructEvent(req.body, sig, process.env.STRIPE_WEBHOOK_SECRET);

    if (event.type === 'payment_intent.succeeded') {
      const paymentIntent = event.data.object;
      const bookingId = paymentIntent.metadata.bookingId;

      const booking = await Booking.findById(bookingId);
      if (booking) {
        booking.payment.status = 'paid';
        booking.payment.transactionId = paymentIntent.id;
        booking.payment.paidAmount = paymentIntent.amount / 100;
        booking.payment.paymentDate = new Date();
        booking.status = 'confirmed';
        await booking.save();
      }
    }

    res.json({ received: true });
  } catch (error) {
    console.error('Webhook error:', error);
    res.status(400).json({ message: 'Webhook error' });
  }
};

module.exports = {
  createPaymentIntent,
  confirmPayment,
  createVNPayPayment,
  handleWebhook
};

const Tour = require('../models/Tour');
const multer = require('multer');
const path = require('path');

// Configure multer for image uploads
const storage = multer.diskStorage({
  destination: (req, file, cb) => {
    cb(null, 'uploads/tours');
  },
  filename: (req, file, cb) => {
    const uniqueSuffix = Date.now() + '-' + Math.round(Math.random() * 1E9);
    cb(null, file.fieldname + '-' + uniqueSuffix + path.extname(file.originalname));
  }
});

const upload = multer({ storage: storage });

// Get all tours with filtering and pagination
const getTours = async (req, res) => {
  try {
    const {
      page = 1,
      limit = 12,
      destination,
      category,
      minPrice,
      maxPrice,
      duration,
      sortBy = 'createdAt',
      sortOrder = 'desc',
      search
    } = req.query;

    // Build filter object
    const filter = { status: 'published' };

    if (destination) filter.destination = new RegExp(destination, 'i');
    if (category) filter.category = category;
    if (minPrice || maxPrice) {
      filter['pricing.basePrice'] = {};
      if (minPrice) filter['pricing.basePrice'].$gte = Number(minPrice);
      if (maxPrice) filter['pricing.basePrice'].$lte = Number(maxPrice);
    }
    if (duration) {
      filter['duration.days'] = Number(duration);
    }
    if (search) {
      filter.$or = [
        { title: new RegExp(search, 'i') },
        { description: new RegExp(search, 'i') },
        { destination: new RegExp(search, 'i') }
      ];
    }

    // Sort options
    const sortOptions = {};
    sortOptions[sortBy] = sortOrder === 'desc' ? -1 : 1;

    const tours = await Tour.find(filter)
      .sort(sortOptions)
      .limit(limit * 1)
      .skip((page - 1) * limit)
      .select('-__v');

    const total = await Tour.countDocuments(filter);

    res.json({
      tours,
      pagination: {
        currentPage: Number(page),
        totalPages: Math.ceil(total / limit),
        totalTours: total,
        hasNext: page < Math.ceil(total / limit),
        hasPrev: page > 1
      }
    });
  } catch (error) {
    console.error('Get tours error:', error);
    res.status(500).json({ message: 'Server error fetching tours' });
  }
};

// Get featured tours
const getFeaturedTours = async (req, res) => {
  try {
    const tours = await Tour.find({ 
      status: 'published', 
      featured: true 
    })
    .sort({ 'ratings.average': -1 })
    .limit(4)
    .select('title shortDescription images pricing duration destination ratings');

    res.json({ tours });
  } catch (error) {
    console.error('Get featured tours error:', error);
    res.status(500).json({ message: 'Server error fetching featured tours' });
  }
};

// Get single tour by slug
const getTourBySlug = async (req, res) => {
  try {
    const { slug } = req.params;

    const tour = await Tour.findOne({ slug, status: 'published' });

    if (!tour) {
      return res.status(404).json({ message: 'Tour not found' });
    }

    res.json({ tour });
  } catch (error) {
    console.error('Get tour by slug error:', error);
    res.status(500).json({ message: 'Server error fetching tour' });
  }
};

// Create new tour (Admin only)
const createTour = async (req, res) => {
  try {
    const tourData = {
      ...req.body,
      createdBy: req.user.id
    };

    // Parse JSON fields if they come as strings
    if (typeof tourData.itinerary === 'string') {
      tourData.itinerary = JSON.parse(tourData.itinerary);
    }
    if (typeof tourData.pricing === 'string') {
      tourData.pricing = JSON.parse(tourData.pricing);
    }

    const tour = new Tour(tourData);
    await tour.save();

    res.status(201).json({
      message: 'Tour created successfully',
      tour
    });
  } catch (error) {
    console.error('Create tour error:', error);
    res.status(500).json({ message: 'Server error creating tour' });
  }
};

// Update tour (Admin only)
const updateTour = async (req, res) => {
  try {
    const { id } = req.params;

    const tour = await Tour.findByIdAndUpdate(
      id,
      { $set: req.body },
      { new: true, runValidators: true }
    );

    if (!tour) {
      return res.status(404).json({ message: 'Tour not found' });
    }

    res.json({
      message: 'Tour updated successfully',
      tour
    });
  } catch (error) {
    console.error('Update tour error:', error);
    res.status(500).json({ message: 'Server error updating tour' });
  }
};

// Delete tour (Admin only)
const deleteTour = async (req, res) => {
  try {
    const { id } = req.params;

    const tour = await Tour.findByIdAndDelete(id);

    if (!tour) {
      return res.status(404).json({ message: 'Tour not found' });
    }

    res.json({ message: 'Tour deleted successfully' });
  } catch (error) {
    console.error('Delete tour error:', error);
    res.status(500).json({ message: 'Server error deleting tour' });
  }
};

// Check tour availability
const checkAvailability = async (req, res) => {
  try {
    const { tourId } = req.params;
    const { startDate, groupSize = 1 } = req.query;

    const tour = await Tour.findById(tourId);

    if (!tour) {
      return res.status(404).json({ message: 'Tour not found' });
    }

    const isAvailable = tour.checkAvailability(new Date(startDate), Number(groupSize));
    const price = tour.calculatePrice(new Date(startDate), Number(groupSize));

    res.json({
      available: isAvailable,
      price,
      currency: tour.pricing.currency
    });
  } catch (error) {
    console.error('Check availability error:', error);
    res.status(500).json({ message: 'Server error checking availability' });
  }
};

module.exports = {
  getTours,
  getFeaturedTours,
  getTourBySlug,
  createTour,
  updateTour,
  deleteTour,
  checkAvailability,
  upload
};

const jwt = require('jsonwebtoken');
const User = require('../models/User');

const auth = async (req, res, next) => {
  try {
    const token = req.header('Authorization')?.replace('Bearer ', '');

    if (!token) {
      return res.status(401).json({ message: 'Access denied. No token provided.' });
    }

    const decoded = jwt.verify(token, process.env.JWT_SECRET);
    const user = await User.findById(decoded.id).select('-password');

    if (!user) {
      return res.status(401).json({ message: 'Invalid token.' });
    }

    req.user = user;
    next();
  } catch (error) {
    res.status(401).json({ message: 'Invalid token.' });
  }
};

const adminAuth = async (req, res, next) => {
  try {
    await auth(req, res, () => {});

    if (req.user.role !== 'admin') {
      return res.status(403).json({ message: 'Access denied. Admin privileges required.' });
    }

    next();
  } catch (error) {
    res.status(401).json({ message: 'Authentication failed.' });
  }
};

const editorAuth = async (req, res, next) => {
  try {
    await auth(req, res, () => {});

    if (!['admin', 'editor'].includes(req.user.role)) {
      return res.status(403).json({ message: 'Access denied. Editor privileges required.' });
    }

    next();
  } catch (error) {
    res.status(401).json({ message: 'Authentication failed.' });
  }
};

module.exports = { auth, adminAuth, editorAuth };

