                    >
                      <Icon icon={Icons.FiSettings} className="w-4 h-4 mr-2" />
                      My Bookings
                    </Link>
                    {state.user?.role === 'admin' && (
                      <Link
                        to="/admin"
                        className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
