import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useQuery, useMutation } from '@tanstack/react-query';
import { useForm, useFieldArray } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import { toursAPI, bookingsAPI } from '../utils/api';
import { BookingForm, Traveler } from '../types';
import { FiCalendar, FiUsers, FiPlus, FiTrash2, FiCreditCard } from '../components/common/Icons';
import toast from 'react-hot-toast';

const schema = yup.object({
  startDate: yup.string().required('Start date is required'),
  numberOfTravelers: yup.object({
    adults: yup.number().min(1, 'At least 1 adult required').required(),
    children: yup.number().min(0).required(),
    infants: yup.number().min(0).required(),
  }),
  travelers: yup.array().of(
    yup.object({
      name: yup.string().required('Name is required'),
      age: yup.number().positive('Age must be positive').optional(),
      type: yup.string().oneOf(['adult', 'child', 'infant']).required(),
    })
  ).required('Travelers information is required'),
  specialRequests: yup.string().notRequired(),
  emergencyContact: yup.object({
    name: yup.string().required('Emergency contact name is required'),
    phone: yup.string().required('Emergency contact phone is required'),
    relationship: yup.string().required('Relationship is required'),
  }).notRequired(),
});

const Booking: React.FC = () => {
  const { tourId } = useParams<{ tourId: string }>();
  const navigate = useNavigate();
  const [currentStep, setCurrentStep] = useState(1);
  const [pricing, setPricing] = useState<any>(null);

  const { data: tourData, isLoading: tourLoading } = useQuery({
    queryKey: ['tour-booking', tourId],
    queryFn: () => toursAPI.getTourBySlug(tourId!),
    enabled: !!tourId,
  });

  const createBookingMutation = useMutation({
    mutationFn: bookingsAPI.createBooking,
    onSuccess: (data) => {
      toast.success('Booking created successfully!');
      navigate('/my-bookings');
    },
    onError: (error: any) => {
      toast.error(error.response?.data?.message || 'Booking failed');
    },
  });

  const {
    register,
    handleSubmit,
    watch,
    control,
    setValue,
    formState: { errors, isSubmitting },
  } = useForm<BookingForm>({
    resolver: yupResolver(schema) as any,
    defaultValues: {
      numberOfTravelers: { adults: 1, children: 0, infants: 0 },
      travelers: [{ name: '', type: 'adult', age: undefined }],
    },
  });

  const { fields, append, remove } = useFieldArray({
    control,
    name: 'travelers',
  });

  const watchedValues = watch();
  const tour = tourData?.data.tour;

  // Update travelers array when number of travelers changes
  useEffect(() => {
    const { adults, children, infants } = watchedValues.numberOfTravelers;
    const totalTravelers = adults + children + infants;
    const currentTravelers = fields.length;

    if (totalTravelers > currentTravelers) {
      // Add travelers
      for (let i = currentTravelers; i < totalTravelers; i++) {
        if (i < adults) {
          append({ name: '', type: 'adult', age: undefined });
        } else if (i < adults + children) {
          append({ name: '', type: 'child', age: undefined });
        } else {
          append({ name: '', type: 'infant', age: undefined });
        }
      }
    } else if (totalTravelers < currentTravelers) {
      // Remove travelers
      for (let i = currentTravelers - 1; i >= totalTravelers; i--) {
        remove(i);
      }
    }
  }, [watchedValues.numberOfTravelers, fields.length, append, remove]);

  // Calculate pricing when form changes
  useEffect(() => {
    if (tour && watchedValues.startDate && watchedValues.numberOfTravelers) {
      const totalTravelers = watchedValues.numberOfTravelers.adults +
                           watchedValues.numberOfTravelers.children +
                           watchedValues.numberOfTravelers.infants;

      // Simulate pricing calculation
      const basePrice = tour.pricing.basePrice;
      const subtotal = basePrice * totalTravelers;
      const taxes = subtotal * 0.1;
      const total = subtotal + taxes;

      setPricing({
        basePrice,
        subtotal,
        taxes,
        total,
        totalTravelers,
      });
    }
  }, [tour, watchedValues.startDate, watchedValues.numberOfTravelers]);

  const onSubmit = (data: BookingForm) => {
    if (!tour) return;

    const bookingData = {
      ...data,
      tourId: tour._id,
    };

    createBookingMutation.mutate(bookingData);
  };

  const nextStep = () => {
    if (currentStep < 3) setCurrentStep(currentStep + 1);
  };

  const prevStep = () => {
    if (currentStep > 1) setCurrentStep(currentStep - 1);
  };

  if (tourLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  if (!tour) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-gray-900 mb-4">Tour Not Found</h1>
          <p className="text-gray-600">The tour you're trying to book doesn't exist.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Progress Steps */}
        <div className="mb-8">
          <div className="flex items-center justify-center">
            {[1, 2, 3].map((step) => (
              <div key={step} className="flex items-center">
                <div
                  className={`w-10 h-10 rounded-full flex items-center justify-center font-medium ${
                    step <= currentStep
                      ? 'bg-primary-600 text-white'
                      : 'bg-gray-300 text-gray-500'
                  }`}
                >
                  {step}
                </div>
                {step < 3 && (
                  <div
                    className={`w-16 h-1 mx-2 ${
                      step < currentStep ? 'bg-primary-600' : 'bg-gray-300'
                    }`}
                  />
                )}
              </div>
            ))}
          </div>
          <div className="flex justify-center mt-4">
            <div className="text-center">
              <p className="text-sm text-gray-600">
                Step {currentStep} of 3: {
                  currentStep === 1 ? 'Tour Configuration' :
                  currentStep === 2 ? 'Traveler Information' :
                  'Payment & Confirmation'
                }
              </p>
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Main Form */}
          <div className="lg:col-span-2">
            <form onSubmit={handleSubmit(onSubmit as any)}>
              {/* Step 1: Tour Configuration */}
              {currentStep === 1 && (
                <div className="bg-white rounded-lg shadow-lg p-6">
                  <h2 className="text-2xl font-bold text-gray-900 mb-6">Tour Configuration</h2>

                  {/* Start Date */}
                  <div className="mb-6">
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Start Date
                    </label>
                    <div className="relative">
                      <FiCalendar className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                      <input
                        {...register('startDate')}
                        type="date"
                        className={`input-field pl-10 ${errors.startDate ? 'border-red-500' : ''}`}
                        min={new Date().toISOString().split('T')[0]}
                      />
                    </div>
                    {errors.startDate && (
                      <p className="mt-1 text-sm text-red-600">{errors.startDate.message}</p>
                    )}
                  </div>

                  {/* Number of Travelers */}
                  <div className="mb-6">
                    <label className="block text-sm font-medium text-gray-700 mb-4">
                      Number of Travelers
                    </label>
                    <div className="grid grid-cols-3 gap-4">
                      <div>
                        <label className="block text-sm text-gray-600 mb-1">Adults (18+)</label>
                        <input
                          {...register('numberOfTravelers.adults')}
                          type="number"
                          min="1"
                          className="input-field"
                        />
                      </div>
                      <div>
                        <label className="block text-sm text-gray-600 mb-1">Children (2-17)</label>
                        <input
                          {...register('numberOfTravelers.children')}
                          type="number"
                          min="0"
                          className="input-field"
                        />
                      </div>
                      <div>
                        <label className="block text-sm text-gray-600 mb-1">Infants (0-2)</label>
                        <input
                          {...register('numberOfTravelers.infants')}
                          type="number"
                          min="0"
                          className="input-field"
                        />
                      </div>
                    </div>
                    {errors.numberOfTravelers?.adults && (
                      <p className="mt-1 text-sm text-red-600">{errors.numberOfTravelers.adults.message}</p>
                    )}
                  </div>

                  <div className="flex justify-end">
                    <button
                      type="button"
                      onClick={nextStep}
                      className="btn-primary"
                      disabled={!watchedValues.startDate || !pricing}
                    >
                      Next: Traveler Information
                    </button>
                  </div>
                </div>
              )}

              {/* Step 2: Traveler Information */}
              {currentStep === 2 && (
                <div className="bg-white rounded-lg shadow-lg p-6">
                  <h2 className="text-2xl font-bold text-gray-900 mb-6">Traveler Information</h2>

                  {/* Travelers */}
                  <div className="space-y-6 mb-8">
                    {fields.map((field, index) => (
                      <div key={field.id} className="border border-gray-200 rounded-lg p-4">
                        <h3 className="text-lg font-medium text-gray-900 mb-4">
                          Traveler {index + 1} ({field.type})
                        </h3>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                          <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                              Full Name
                            </label>
                            <input
                              {...register(`travelers.${index}.name`)}
                              type="text"
                              className="input-field"
                              placeholder="Enter full name"
                            />
                            {errors.travelers?.[index]?.name && (
                              <p className="mt-1 text-sm text-red-600">
                                {errors.travelers[index]?.name?.message}
                              </p>
                            )}
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                              Age
                            </label>
                            <input
                              {...register(`travelers.${index}.age`)}
                              type="number"
                              className="input-field"
                              placeholder="Age"
                            />
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>

                  {/* Emergency Contact */}
                  <div className="border-t pt-6">
                    <h3 className="text-lg font-medium text-gray-900 mb-4">Emergency Contact</h3>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Name
                        </label>
                        <input
                          {...register('emergencyContact.name')}
                          type="text"
                          className="input-field"
                          placeholder="Contact name"
                        />
                        {errors.emergencyContact?.name && (
                          <p className="mt-1 text-sm text-red-600">
                            {errors.emergencyContact.name.message}
                          </p>
                        )}
                      </div>
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Phone
                        </label>
                        <input
                          {...register('emergencyContact.phone')}
                          type="tel"
                          className="input-field"
                          placeholder="Phone number"
                        />
                        {errors.emergencyContact?.phone && (
                          <p className="mt-1 text-sm text-red-600">
                            {errors.emergencyContact.phone.message}
                          </p>
                        )}
                      </div>
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Relationship
                        </label>
                        <select
                          {...register('emergencyContact.relationship')}
                          className="input-field"
                        >
                          <option value="">Select relationship</option>
                          <option value="spouse">Spouse</option>
                          <option value="parent">Parent</option>
                          <option value="child">Child</option>
                          <option value="sibling">Sibling</option>
                          <option value="friend">Friend</option>
                          <option value="other">Other</option>
                        </select>
                        {errors.emergencyContact?.relationship && (
                          <p className="mt-1 text-sm text-red-600">
                            {errors.emergencyContact.relationship.message}
                          </p>
                        )}
                      </div>
                    </div>
                  </div>

                  {/* Special Requests */}
                  <div className="mt-6">
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Special Requests (Optional)
                    </label>
                    <textarea
                      {...register('specialRequests')}
                      rows={3}
                      className="input-field"
                      placeholder="Any dietary requirements, accessibility needs, or special requests..."
                    />
                  </div>

                  <div className="flex justify-between mt-6">
                    <button
                      type="button"
                      onClick={prevStep}
                      className="btn-secondary"
                    >
                      Back
                    </button>
                    <button
                      type="button"
                      onClick={nextStep}
                      className="btn-primary"
                    >
                      Next: Payment
                    </button>
                  </div>
                </div>
              )}

              {/* Step 3: Payment */}
              {currentStep === 3 && (
                <div className="bg-white rounded-lg shadow-lg p-6">
                  <h2 className="text-2xl font-bold text-gray-900 mb-6">Payment & Confirmation</h2>

                  <div className="mb-6">
                    <h3 className="text-lg font-medium text-gray-900 mb-4">Payment Options</h3>
                    <div className="space-y-3">
                      <label className="flex items-center p-4 border border-gray-300 rounded-lg cursor-pointer hover:bg-gray-50">
                        <input type="radio" name="paymentMethod" value="credit_card" className="mr-3" defaultChecked />
                        <FiCreditCard className="w-5 h-5 mr-2" />
                        <span>Credit Card (Stripe)</span>
                      </label>
                      <label className="flex items-center p-4 border border-gray-300 rounded-lg cursor-pointer hover:bg-gray-50">
                        <input type="radio" name="paymentMethod" value="vnpay" className="mr-3" />
                        <span>VNPay (Vietnamese QR Code)</span>
                      </label>
                      <label className="flex items-center p-4 border border-gray-300 rounded-lg cursor-pointer hover:bg-gray-50">
                        <input type="radio" name="paymentMethod" value="bank_transfer" className="mr-3" />
                        <span>Bank Transfer</span>
                      </label>
                    </div>
                  </div>

                  <div className="flex justify-between">
                    <button
                      type="button"
                      onClick={prevStep}
                      className="btn-secondary"
                    >
                      Back
                    </button>
                    <button
                      type="submit"
                      disabled={isSubmitting}
                      className="btn-primary disabled:opacity-50"
                    >
                      {isSubmitting ? 'Creating Booking...' : 'Confirm Booking'}
                    </button>
                  </div>
                </div>
              )}
            </form>
          </div>

          {/* Booking Summary */}
          <div className="lg:col-span-1">
            <div className="sticky top-4">
              <div className="bg-white rounded-lg shadow-lg p-6">
                <h3 className="text-lg font-bold text-gray-900 mb-4">Booking Summary</h3>

                {tour && (
                  <>
                    <div className="mb-4">
                      <img
                        src={tour.images[0]?.url || `https://images.unsplash.com/photo-1469474968028-56623f02e42e?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80`}
                        alt={tour.title}
                        className="w-full h-32 object-cover rounded-lg"
                      />
                    </div>

                    <h4 className="font-semibold text-gray-900 mb-2">{tour.title}</h4>
                    <p className="text-sm text-gray-600 mb-4">{tour.destination}</p>

                    {watchedValues.startDate && (
                      <div className="space-y-2 text-sm">
                        <div className="flex justify-between">
                          <span>Start Date:</span>
                          <span>{new Date(watchedValues.startDate).toLocaleDateString()}</span>
                        </div>
                        <div className="flex justify-between">
                          <span>Duration:</span>
                          <span>{tour.duration.days}D/{tour.duration.nights}N</span>
                        </div>
                      </div>
                    )}
                  </>
                )}

                {pricing && (
                  <div className="border-t pt-4 mt-4">
                    <div className="space-y-2 text-sm">
                      <div className="flex justify-between">
                        <span>Base Price x {pricing.totalTravelers}:</span>
                        <span>${pricing.subtotal}</span>
                      </div>
                      <div className="flex justify-between">
                        <span>Taxes & Fees:</span>
                        <span>${pricing.taxes.toFixed(2)}</span>
                      </div>
                      <div className="border-t pt-2 font-bold">
                        <div className="flex justify-between">
                          <span>Total:</span>
                          <span>${pricing.total.toFixed(2)}</span>
                        </div>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Booking;

import React, { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Link } from 'react-router-dom';
import { bookingsAPI } from '../utils/api';
import { Booking } from '../types';
import { 
  FiFilter, FiCalendar, FiMapPin, FiClock, FiUsers, 
  FiCreditCard, FiEye, FiX, FiDownload 
} from '../components/common/Icons';
import { format } from 'date-fns';

const MyBookings: React.FC = () => {
  const [statusFilter, setStatusFilter] = useState<string>('');
  const [currentPage, setCurrentPage] = useState(1);

  const { data: bookingsData, isLoading, error } = useQuery({
    queryKey: ['user-bookings', currentPage, statusFilter],
    queryFn: () => bookingsAPI.getUserBookings(currentPage, statusFilter || undefined),
  });

  const bookings = bookingsData?.data.data || [];
  const pagination = bookingsData?.data.pagination;

  const statusOptions = [
    { value: '', label: 'All Bookings' },
    { value: 'pending', label: 'Pending' },
    { value: 'confirmed', label: 'Confirmed' },
    { value: 'in_progress', label: 'In Progress' },
    { value: 'completed', label: 'Completed' },
    { value: 'cancelled', label: 'Cancelled' },
  ];

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending':
        return 'bg-yellow-100 text-yellow-800';
      case 'confirmed':
        return 'bg-blue-100 text-blue-800';
      case 'in_progress':
        return 'bg-green-100 text-green-800';
      case 'completed':
        return 'bg-gray-100 text-gray-800';
      case 'cancelled':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getPaymentStatusColor = (status: string) => {
    switch (status) {
      case 'paid':
        return 'bg-green-100 text-green-800';
      case 'pending':
        return 'bg-yellow-100 text-yellow-800';
      case 'failed':
        return 'bg-red-100 text-red-800';
      case 'refunded':
        return 'bg-purple-100 text-purple-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="animate-pulse space-y-6">
            {[...Array(3)].map((_, index) => (
              <div key={index} className="bg-white rounded-lg shadow-lg p-6">
                <div className="h-4 bg-gray-300 rounded w-1/4 mb-4"></div>
                <div className="h-6 bg-gray-300 rounded w-1/2 mb-2"></div>
                <div className="h-4 bg-gray-300 rounded w-1/3"></div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">My Bookings</h1>
          <p className="text-gray-600">Manage and view all your travel bookings in one place</p>
        </div>

        {/* Filters */}
        <div className="bg-white rounded-lg shadow-lg p-6 mb-8">
          <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
            <div className="flex items-center gap-4">
              <FiFilter className="w-5 h-5 text-gray-400" />
              <select
                value={statusFilter}
                onChange={(e) => {
                  setStatusFilter(e.target.value);
                  setCurrentPage(1);
                }}
                className="input-field min-w-48"
              >
                {statusOptions.map(option => (
                  <option key={option.value} value={option.value}>
                    {option.label}
                  </option>
                ))}
              </select>
            </div>

            {pagination && (
              <p className="text-sm text-gray-600">
                Showing {((pagination.currentPage - 1) * 10) + 1}-{Math.min(pagination.currentPage * 10, pagination.totalBookings || pagination.totalItems)} of {pagination.totalBookings || pagination.totalItems} bookings
              </p>
            )}
          </div>
        </div>

        {/* Bookings List */}
        {error ? (
          <div className="bg-white rounded-lg shadow-lg p-12 text-center">
            <p className="text-gray-500">Error loading bookings. Please try again.</p>
          </div>
        ) : !bookings.length ? (
          <div className="bg-white rounded-lg shadow-lg p-12 text-center">
            <FiCalendar className="w-16 h-16 text-gray-300 mx-auto mb-4" />
            <h3 className="text-xl font-semibold text-gray-900 mb-2">No bookings found</h3>
            <p className="text-gray-600 mb-6">
              {statusFilter
                ? `No ${statusFilter} bookings found. Try adjusting your filters.`
                : "You haven't made any bookings yet. Start exploring our amazing tours!"
              }
            </p>
            <Link to="/tours" className="btn-primary">
              Browse Tours
            </Link>
          </div>
        ) : (
          <div className="space-y-6">
            {bookings.map((booking: Booking) => (
              <BookingCard key={booking._id} booking={booking} getStatusColor={getStatusColor} getPaymentStatusColor={getPaymentStatusColor} />
            ))}
          </div>
        )}

        {/* Pagination */}
        {pagination && pagination.totalPages > 1 && (
          <div className="flex justify-center mt-8">
            <nav className="flex items-center space-x-2">
              <button
                onClick={() => setCurrentPage(pagination.currentPage - 1)}
                disabled={!pagination.hasPrev}
                className="px-3 py-2 text-sm font-medium text-gray-500 bg-white border border-gray-300 rounded-md hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Previous
              </button>

              {[...Array(Math.min(5, pagination.totalPages))].map((_, index) => {
                const page = index + 1;
                return (
                  <button
                    key={page}
                    onClick={() => setCurrentPage(page)}
                    className={`px-3 py-2 text-sm font-medium rounded-md ${
                      page === pagination.currentPage
                        ? 'bg-primary-600 text-white'
                        : 'text-gray-500 bg-white border border-gray-300 hover:bg-gray-50'
                    }`}
                  >
                    {page}
                  </button>
                );
              })}

              <button
                onClick={() => setCurrentPage(pagination.currentPage + 1)}
                disabled={!pagination.hasNext}
                className="px-3 py-2 text-sm font-medium text-gray-500 bg-white border border-gray-300 rounded-md hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Next
              </button>
            </nav>
          </div>
        )}
      </div>
    </div>
  );
};

interface BookingCardProps {
  booking: Booking;
  getStatusColor: (status: string) => string;
  getPaymentStatusColor: (status: string) => string;
}

const BookingCard: React.FC<BookingCardProps> = ({ booking, getStatusColor, getPaymentStatusColor }) => {
  const [showDetails, setShowDetails] = useState(false);

  return (
    <div className="bg-white rounded-lg shadow-lg overflow-hidden">
      <div className="p-6">
        <div className="flex flex-col lg:flex-row lg:items-center justify-between mb-4">
          <div className="flex items-center space-x-4 mb-4 lg:mb-0">
            <div className="w-16 h-16 bg-gray-200 rounded-lg overflow-hidden flex-shrink-0">
              {booking.tour.images?.[0]?.url ? (
                <img
                  src={booking.tour.images[0].url}
                  alt={booking.tour.title}
                  className="w-full h-full object-cover"
                />
              ) : (
                <div className="w-full h-full bg-gray-300 flex items-center justify-center">
                  <FiMapPin className="w-6 h-6 text-gray-500" />
                </div>
              )}
            </div>
            <div>
              <h3 className="text-lg font-semibold text-gray-900 mb-1">
                {booking.tour.title}
              </h3>
              <p className="text-sm text-gray-600 flex items-center">
                <FiMapPin className="w-4 h-4 mr-1" />
                {booking.tour.destination}
              </p>
              <p className="text-sm text-gray-500">
                Booking #{booking.bookingNumber}
              </p>
            </div>
          </div>

          <div className="flex flex-col sm:flex-row items-start sm:items-center space-y-2 sm:space-y-0 sm:space-x-4">
            <div className="flex space-x-2">
              <span className={`px-3 py-1 rounded-full text-xs font-medium ${getStatusColor(booking.status)}`}>
                {booking.status.charAt(0).toUpperCase() + booking.status.slice(1)}
              </span>
              <span className={`px-3 py-1 rounded-full text-xs font-medium ${getPaymentStatusColor(booking.payment.status)}`}>
                {booking.payment.status.charAt(0).toUpperCase() + booking.payment.status.slice(1)}
              </span>
            </div>
            <div className="text-right">
              <div className="text-lg font-bold text-primary-600">
                ${booking.pricing.totalAmount.toFixed(2)}
              </div>
              <div className="text-sm text-gray-500">
                {booking.pricing.currency}
              </div>
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 mb-4">
          <div className="flex items-center text-sm text-gray-600">
            <FiCalendar className="w-4 h-4 mr-2" />
            <div>
              <div className="font-medium">Start Date</div>
              <div>{format(new Date(booking.bookingDetails.startDate), 'MMM dd, yyyy')}</div>
            </div>
          </div>

          <div className="flex items-center text-sm text-gray-600">
            <FiClock className="w-4 h-4 mr-2" />
            <div>
              <div className="font-medium">Duration</div>
              <div>{booking.tour.duration.days}D/{booking.tour.duration.nights}N</div>
            </div>
          </div>

          <div className="flex items-center text-sm text-gray-600">
            <FiUsers className="w-4 h-4 mr-2" />
            <div>
              <div className="font-medium">Travelers</div>
              <div>{booking.bookingDetails.totalTravelers} people</div>
            </div>
          </div>

          <div className="flex items-center text-sm text-gray-600">
            <FiCreditCard className="w-4 h-4 mr-2" />
            <div>
              <div className="font-medium">Payment</div>
              <div>{booking.payment.method || 'Not specified'}</div>
            </div>
          </div>
        </div>

        <div className="flex flex-wrap gap-2">
          <button
            onClick={() => setShowDetails(!showDetails)}
            className="btn-secondary text-sm flex items-center"
          >
            <FiEye className="w-4 h-4 mr-1" />
            {showDetails ? 'Hide Details' : 'View Details'}
          </button>

          {booking.status !== 'cancelled' && booking.status !== 'completed' && (
            <button className="text-sm px-4 py-2 text-red-600 border border-red-600 rounded-lg hover:bg-red-50 flex items-center">
              <FiX className="w-4 h-4 mr-1" />
              Cancel Booking
            </button>
          )}

          <button className="text-sm px-4 py-2 text-gray-600 border border-gray-300 rounded-lg hover:bg-gray-50 flex items-center">
            <FiDownload className="w-4 h-4 mr-1" />
            Download Invoice
          </button>
        </div>

        {/* Expanded Details */}
        {showDetails && (
          <div className="mt-6 pt-6 border-t border-gray-200">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {/* Travelers */}
              <div>
                <h4 className="font-semibold text-gray-900 mb-3">Travelers</h4>
                <div className="space-y-2">
                  {booking.travelers.map((traveler, index) => (
                    <div key={index} className="flex justify-between items-center text-sm">
                      <span>{traveler.name}</span>
                      <span className="text-gray-500 capitalize">
                        {traveler.type} {traveler.age && `(${traveler.age}y)`}
                      </span>
                    </div>
                  ))}
                </div>
              </div>

              {/* Emergency Contact */}
              {booking.emergencyContact && (
                <div>
                  <h4 className="font-semibold text-gray-900 mb-3">Emergency Contact</h4>
                  <div className="text-sm space-y-1">
                    <div><span className="font-medium">Name:</span> {booking.emergencyContact.name}</div>
                    <div><span className="font-medium">Phone:</span> {booking.emergencyContact.phone}</div>
                    <div><span className="font-medium">Relationship:</span> {booking.emergencyContact.relationship}</div>
                  </div>
                </div>
              )}

              {/* Special Requests */}
              {booking.specialRequests && (
                <div className="lg:col-span-2">
                  <h4 className="font-semibold text-gray-900 mb-3">Special Requests</h4>
                  <p className="text-sm text-gray-600">{booking.specialRequests}</p>
                </div>
              )}

              {/* Payment Details */}
              <div className="lg:col-span-2">
                <h4 className="font-semibold text-gray-900 mb-3">Payment Details</h4>
                <div className="bg-gray-50 rounded-lg p-4">
                  <div className="grid grid-cols-2 gap-4 text-sm">
                    <div className="flex justify-between">
                      <span>Subtotal:</span>
                      <span>${booking.pricing.subtotal.toFixed(2)}</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Taxes & Fees:</span>
                      <span>${booking.pricing.taxes.toFixed(2)}</span>
                    </div>
                    <div className="flex justify-between font-semibold border-t pt-2">
                      <span>Total:</span>
                      <span>${booking.pricing.totalAmount.toFixed(2)}</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Paid Amount:</span>
                      <span>${booking.payment.paidAmount.toFixed(2)}</span>
                    </div>
                  </div>
                  {booking.payment.transactionId && (
                    <div className="mt-2 text-xs text-gray-500">
                      Transaction ID: {booking.payment.transactionId}
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default MyBookings;

import React, { useState } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import { useAuth } from '../contexts/AuthContext';
import { authAPI } from '../utils/api';
import { User } from '../types';
import { FiUser, FiMail, FiPhone, FiMapPin, FiSettings, FiLock, FiSave, FiEdit3 } from '../components/common/Icons';
import toast from 'react-hot-toast';

const profileSchema = yup.object({
  name: yup.string().min(2, 'Name must be at least 2 characters').required('Name is required'),
  email: yup.string().email('Invalid email').required('Email is required'),
  phone: yup.string().optional(),
  country: yup.string().optional(),
});

const passwordSchema = yup.object({
  currentPassword: yup.string().required('Current password is required'),
  newPassword: yup.string().min(6, 'Password must be at least 6 characters').required('New password is required'),
  confirmPassword: yup.string()
    .oneOf([yup.ref('newPassword')], 'Passwords must match')
    .required('Please confirm your password'),
});

interface ProfileFormData {
  name: string;
  email: string;
  phone?: string;
  country?: string;
}

interface PasswordFormData {
  currentPassword: string;
  newPassword: string;
  confirmPassword: string;
}

const Profile: React.FC = () => {
  const { state, updateProfile } = useAuth();
  const [activeTab, setActiveTab] = useState('profile');
  const [isEditing, setIsEditing] = useState(false);

  const { data: profileData, refetch } = useQuery({
    queryKey: ['profile'],
    queryFn: authAPI.getProfile,
    enabled: !!state.user,
  });

  const updateProfileMutation = useMutation({
    mutationFn: authAPI.updateProfile,
    onSuccess: (data) => {
      updateProfile(data.data.user);
      setIsEditing(false);
      toast.success('Profile updated successfully!');
      refetch();
    },
    onError: (error: any) => {
      toast.error(error.response?.data?.message || 'Failed to update profile');
    },
  });

  const changePasswordMutation = useMutation({
    mutationFn: authAPI.changePassword,
    onSuccess: () => {
      toast.success('Password changed successfully!');
      resetPassword();
    },
    onError: (error: any) => {
      toast.error(error.response?.data?.message || 'Failed to change password');
    },
  });

  const {
    register: registerProfile,
    handleSubmit: handleProfileSubmit,
    formState: { errors: profileErrors, isSubmitting: isUpdatingProfile },
    reset: resetProfile,
  } = useForm<ProfileFormData>({
    resolver: yupResolver(profileSchema) as any,
    defaultValues: {
      name: profileData?.data.user.name || '',
      email: profileData?.data.user.email || '',
      phone: profileData?.data.user.phone || '',
      country: profileData?.data.user.country || '',
    },
  });

  const {
    register: registerPassword,
    handleSubmit: handlePasswordSubmit,
    formState: { errors: passwordErrors, isSubmitting: isChangingPassword },
    reset: resetPassword,
  } = useForm<PasswordFormData>({
    resolver: yupResolver(passwordSchema),
  });

  React.useEffect(() => {
    if (profileData?.data.user) {
      resetProfile({
        name: profileData.data.user.name,
        email: profileData.data.user.email,
        phone: profileData.data.user.phone || '',
        country: profileData.data.user.country || '',
      });
    }
  }, [profileData, resetProfile]);

  const onProfileSubmit = (data: ProfileFormData) => {
    updateProfileMutation.mutate(data);
  };

  const onPasswordSubmit = (data: PasswordFormData) => {
    changePasswordMutation.mutate({
      currentPassword: data.currentPassword,
      newPassword: data.newPassword,
    });
  };

  const user = profileData?.data.user;

  if (!user) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  const tabs = [
    { id: 'profile', name: 'Profile Information', icon: FiUser },
    { id: 'security', name: 'Security', icon: FiLock },
    { id: 'preferences', name: 'Preferences', icon: FiSettings },
  ];

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="bg-white rounded-lg shadow-lg p-6 mb-8">
          <div className="flex items-center">
            <div className="w-20 h-20 bg-primary-100 rounded-full flex items-center justify-center mr-6">
              {user.avatar ? (
                <img
                  src={user.avatar}
                  alt={user.name}
                  className="w-20 h-20 rounded-full object-cover"
                />
              ) : (
                <FiUser className="w-10 h-10 text-primary-600" />
              )}
            </div>
            <div>
              <h1 className="text-3xl font-bold text-gray-900">{user.name}</h1>
              <p className="text-gray-600">{user.email}</p>
              <div className="flex items-center mt-2">
                <span className={`px-3 py-1 rounded-full text-xs font-medium ${
                  user.role === 'admin' ? 'bg-red-100 text-red-800' :
                  user.role === 'editor' ? 'bg-blue-100 text-blue-800' :
                  'bg-green-100 text-green-800'
                }`}>
                  {user.role.charAt(0).toUpperCase() + user.role.slice(1)}
                </span>
                {user.isVerified && (
                  <span className="ml-2 px-3 py-1 bg-green-100 text-green-800 rounded-full text-xs font-medium">
                    Verified
                  </span>
                )}
              </div>
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
          {/* Sidebar */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-lg shadow-lg">
              <nav className="space-y-1 p-2">
                {tabs.map((tab) => (
                  <button
                    key={tab.id}
                    onClick={() => setActiveTab(tab.id)}
                    className={`w-full flex items-center px-4 py-3 text-sm font-medium rounded-lg transition-colors ${
                      activeTab === tab.id
                        ? 'bg-primary-100 text-primary-700'
                        : 'text-gray-600 hover:bg-gray-50'
                    }`}
                  >
                    <tab.icon className="w-5 h-5 mr-3" />
                    {tab.name}
                  </button>
                ))}
              </nav>
            </div>
          </div>

          {/* Main Content */}
          <div className="lg:col-span-3">
            {/* Profile Information Tab */}
            {activeTab === 'profile' && (
              <div className="bg-white rounded-lg shadow-lg p-6">
                <div className="flex items-center justify-between mb-6">
                  <h2 className="text-2xl font-bold text-gray-900">Profile Information</h2>
                  <button
                    onClick={() => setIsEditing(!isEditing)}
                    className="btn-secondary flex items-center"
                  >
                    <FiEdit3 className="w-4 h-4 mr-2" />
                    {isEditing ? 'Cancel' : 'Edit'}
                  </button>
                </div>

                <form onSubmit={handleProfileSubmit(onProfileSubmit)}>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Full Name
                      </label>
                      <div className="relative">
                        <FiUser className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                        <input
                          {...registerProfile('name')}
                          type="text"
                          disabled={!isEditing}
                          className={`input-field pl-10 ${!isEditing ? 'bg-gray-50 cursor-not-allowed' : ''} ${
                            profileErrors.name ? 'border-red-500' : ''
                          }`}
                        />
                      </div>
                      {profileErrors.name && (
                        <p className="mt-1 text-sm text-red-600">{profileErrors.name.message}</p>
                      )}
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Email Address
                      </label>
                      <div className="relative">
                        <FiMail className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                        <input
                          {...registerProfile('email')}
                          type="email"
                          disabled={!isEditing}
                          className={`input-field pl-10 ${!isEditing ? 'bg-gray-50 cursor-not-allowed' : ''} ${
                            profileErrors.email ? 'border-red-500' : ''
                          }`}
                        />
                      </div>
                      {profileErrors.email && (
                        <p className="mt-1 text-sm text-red-600">{profileErrors.email.message}</p>
                      )}
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Phone Number
                      </label>
                      <div className="relative">
                        <FiPhone className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                        <input
                          {...registerProfile('phone')}
                          type="tel"
                          disabled={!isEditing}
                          className={`input-field pl-10 ${!isEditing ? 'bg-gray-50 cursor-not-allowed' : ''}`}
                          placeholder="Enter phone number"
                        />
                      </div>
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Country
                      </label>
                      <div className="relative">
                        <FiMapPin className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                        <select
                          {...registerProfile('country')}
                          disabled={!isEditing}
                          className={`input-field pl-10 ${!isEditing ? 'bg-gray-50 cursor-not-allowed' : ''}`}
                        >
                          <option value="">Select country</option>
                          <option value="Vietnam">Vietnam</option>
                          <option value="United States">United States</option>
                          <option value="Canada">Canada</option>
                          <option value="Australia">Australia</option>
                          <option value="United Kingdom">United Kingdom</option>
                          <option value="Germany">Germany</option>
                          <option value="France">France</option>
                          <option value="Japan">Japan</option>
                          <option value="South Korea">South Korea</option>
                          <option value="Singapore">Singapore</option>
                          <option value="Other">Other</option>
                        </select>
                      </div>
                    </div>
                  </div>

                  {isEditing && (
                    <div className="mt-6 flex justify-end">
                      <button
                        type="submit"
                        disabled={isUpdatingProfile}
                        className="btn-primary disabled:opacity-50 flex items-center"
                      >
                        <FiSave className="w-4 h-4 mr-2" />
                        {isUpdatingProfile ? 'Saving...' : 'Save Changes'}
                      </button>
                    </div>
                  )}
                </form>
              </div>
            )}

            {/* Security Tab */}
            {activeTab === 'security' && (
              <div className="bg-white rounded-lg shadow-lg p-6">
                <h2 className="text-2xl font-bold text-gray-900 mb-6">Security Settings</h2>

                <form onSubmit={handlePasswordSubmit(onPasswordSubmit)}>
                  <div className="space-y-6">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Current Password
                      </label>
                      <input
                        {...registerPassword('currentPassword')}
                        type="password"
                        className={`input-field ${passwordErrors.currentPassword ? 'border-red-500' : ''}`}
                        placeholder="Enter current password"
                      />
                      {passwordErrors.currentPassword && (
                        <p className="mt-1 text-sm text-red-600">{passwordErrors.currentPassword.message}</p>
                      )}
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        New Password
                      </label>
                      <input
                        {...registerPassword('newPassword')}
                        type="password"
                        className={`input-field ${passwordErrors.newPassword ? 'border-red-500' : ''}`}
                        placeholder="Enter new password"
                      />
                      {passwordErrors.newPassword && (
                        <p className="mt-1 text-sm text-red-600">{passwordErrors.newPassword.message}</p>
                      )}
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Confirm New Password
                      </label>
                      <input
                        {...registerPassword('confirmPassword')}
                        type="password"
                        className={`input-field ${passwordErrors.confirmPassword ? 'border-red-500' : ''}`}
                        placeholder="Confirm new password"
                      />
                      {passwordErrors.confirmPassword && (
                        <p className="mt-1 text-sm text-red-600">{passwordErrors.confirmPassword.message}</p>
                      )}
                    </div>

                    <div className="flex justify-end">
                      <button
                        type="submit"
                        disabled={isChangingPassword}
                        className="btn-primary disabled:opacity-50 flex items-center"
                      >
                        <FiLock className="w-4 h-4 mr-2" />
                        {isChangingPassword ? 'Changing...' : 'Change Password'}
                      </button>
                    </div>
                  </div>
                </form>
              </div>
            )}

            {/* Preferences Tab */}
            {activeTab === 'preferences' && (
              <div className="bg-white rounded-lg shadow-lg p-6">
                <h2 className="text-2xl font-bold text-gray-900 mb-6">Preferences</h2>

                <div className="space-y-6">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Language
                    </label>
                    <select className="input-field" defaultValue={user.preferences.language}>
                      <option value="en">English</option>
                      <option value="vi">Ting Vit</option>
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Currency
                    </label>
                    <select className="input-field" defaultValue={user.preferences.currency}>
                      <option value="USD">US Dollar (USD)</option>
                      <option value="VND">Vietnamese Dong (VND)</option>
                      <option value="EUR">Euro (EUR)</option>
                      <option value="GBP">British Pound (GBP)</option>
                    </select>
                  </div>

                  <div className="flex items-center">
                    <input
                      type="checkbox"
                      id="newsletter"
                      className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-gray-300 rounded"
                      defaultChecked={user.preferences.newsletter}
                    />
                    <label htmlFor="newsletter" className="ml-2 block text-sm text-gray-900">
                      Subscribe to newsletter and promotional emails
                    </label>
                  </div>

                  <div className="flex justify-end">
                    <button className="btn-primary">
                      Save Preferences
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;

import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useQuery } from '@tanstack/react-query';
import { toursAPI } from '../utils/api';
import { FiStar, FiMapPin, FiClock, FiUsers, FiCheck, FiX, FiCalendar, FiCamera } from '../components/common/Icons';

const TourDetail: React.FC = () => {
  const { slug } = useParams<{ slug: string }>();
  const [selectedImage, setSelectedImage] = useState(0);
  const [showBookingWidget, setShowBookingWidget] = useState(false);

  const { data: tourData, isLoading, error } = useQuery({
    queryKey: ['tour', slug],
    queryFn: () => toursAPI.getTourBySlug(slug!),
    enabled: !!slug,
  });

  const tour = tourData?.data.tour;

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <div className="animate-pulse">
          <div className="h-96 bg-gray-300"></div>
          <div className="max-w-7xl mx-auto px-4 py-8">
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
              <div className="lg:col-span-2 space-y-6">
                <div className="h-8 bg-gray-300 rounded"></div>
                <div className="h-4 bg-gray-300 rounded w-3/4"></div>
                <div className="space-y-2">
                  <div className="h-4 bg-gray-300 rounded"></div>
                  <div className="h-4 bg-gray-300 rounded"></div>
                  <div className="h-4 bg-gray-300 rounded w-2/3"></div>
                </div>
              </div>
              <div className="h-96 bg-gray-300 rounded-lg"></div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (error || !tour) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-gray-900 mb-4">Tour Not Found</h1>
          <p className="text-gray-600 mb-8">The tour you're looking for doesn't exist or has been removed.</p>
          <Link to="/tours" className="btn-primary">
            Browse All Tours
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Image Gallery */}
      <div className="relative h-96 md:h-[500px] overflow-hidden">
        <img
          src={tour.images[selectedImage]?.url || `https://images.unsplash.com/photo-1469474968028-56623f02e42e?ixlib=rb-4.0.3&auto=format&fit=crop&w=2070&q=80`}
          alt={tour.title}
          className="w-full h-full object-cover"
        />
        <div className="absolute inset-0 bg-black bg-opacity-20"></div>

        {/* Image Navigation */}
        {tour.images.length > 1 && (
          <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex space-x-2">
            {tour.images.slice(0, 5).map((_, index) => (
              <button
                key={index}
                onClick={() => setSelectedImage(index)}
                className={`w-3 h-3 rounded-full transition-all ${
                  index === selectedImage ? 'bg-white' : 'bg-white/50'
                }`}
              />
            ))}
            {tour.images.length > 5 && (
              <button className="text-white text-sm bg-black/50 px-2 py-1 rounded">
                <FiCamera className="w-4 h-4 inline mr-1" />
                +{tour.images.length - 5}
              </button>
            )}
          </div>
        )}
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Main Content */}
          <div className="lg:col-span-2">
            {/* Tour Header */}
            <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
              <div className="flex items-center text-sm text-gray-500 mb-2">
                <FiMapPin className="w-4 h-4 mr-1" />
                {tour.destination}
              </div>
              <h1 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">
                {tour.title}
              </h1>

              <div className="flex flex-wrap items-center gap-6 text-sm text-gray-600">
                <div className="flex items-center">
                  <FiClock className="w-4 h-4 mr-1" />
                  {tour.duration.days} Days / {tour.duration.nights} Nights
                </div>
                <div className="flex items-center">
                  <FiUsers className="w-4 h-4 mr-1" />
                  Max {tour.maxGroupSize} People
                </div>
                <div className="flex items-center">
                  <FiStar className="w-4 h-4 text-yellow-400 mr-1" />
                  {tour.ratings.average.toFixed(1)} ({tour.ratings.count} reviews)
                </div>
                <span className={`px-3 py-1 rounded-full text-xs font-medium ${
                  tour.difficulty === 'easy' ? 'bg-green-100 text-green-800' :
                  tour.difficulty === 'moderate' ? 'bg-yellow-100 text-yellow-800' :
                  tour.difficulty === 'challenging' ? 'bg-orange-100 text-orange-800' :
                  'bg-red-100 text-red-800'
                }`}>
                  {tour.difficulty.charAt(0).toUpperCase() + tour.difficulty.slice(1)}
                </span>
              </div>
            </div>

            {/* Description */}
            <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
              <h2 className="text-2xl font-bold text-gray-900 mb-4">About This Tour</h2>
              <div className="prose max-w-none text-gray-600" dangerouslySetInnerHTML={{ __html: tour.description }} />
            </div>

            {/* Highlights */}
            {tour.highlights.length > 0 && (
              <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
                <h2 className="text-2xl font-bold text-gray-900 mb-4">Tour Highlights</h2>
                <ul className="grid grid-cols-1 md:grid-cols-2 gap-3">
                  {tour.highlights.map((highlight, index) => (
                    <li key={index} className="flex items-start">
                      <FiCheck className="w-5 h-5 text-green-500 mr-2 mt-0.5 flex-shrink-0" />
                      <span className="text-gray-600">{highlight}</span>
                    </li>
                  ))}
                </ul>
              </div>
            )}

            {/* Itinerary */}
            <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
              <h2 className="text-2xl font-bold text-gray-900 mb-4">Day by Day Itinerary</h2>
              <div className="space-y-6">
                {tour.itinerary.map((day, index) => (
                  <div key={index} className="border-l-4 border-primary-200 pl-6">
                    <div className="flex items-center mb-2">
                      <div className="w-8 h-8 bg-primary-600 text-white rounded-full flex items-center justify-center text-sm font-bold mr-3">
                        {day.day}
                      </div>
                      <h3 className="text-lg font-semibold text-gray-900">{day.title}</h3>
                    </div>
                    <p className="text-gray-600 mb-3">{day.description}</p>
                    {day.activities.length > 0 && (
                      <div className="mb-2">
                        <span className="text-sm font-medium text-gray-700">Activities: </span>
                        <span className="text-sm text-gray-600">{day.activities.join(', ')}</span>
                      </div>
                    )}
                    {day.meals.length > 0 && (
                      <div className="mb-2">
                        <span className="text-sm font-medium text-gray-700">Meals: </span>
                        <span className="text-sm text-gray-600">{day.meals.join(', ')}</span>
                      </div>
                    )}
                    {day.accommodation && (
                      <div>
                        <span className="text-sm font-medium text-gray-700">Accommodation: </span>
                        <span className="text-sm text-gray-600">{day.accommodation}</span>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </div>

            {/* Inclusions & Exclusions */}
            <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
              <h2 className="text-2xl font-bold text-gray-900 mb-6">What's Included</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                <div>
                  <h3 className="text-lg font-semibold text-green-600 mb-4 flex items-center">
                    <FiCheck className="w-5 h-5 mr-2" />
                    Included
                  </h3>
                  <ul className="space-y-2">
                    {tour.inclusions.map((inclusion, index) => (
                      <li key={index} className="flex items-start">
                        <FiCheck className="w-4 h-4 text-green-500 mr-2 mt-0.5 flex-shrink-0" />
                        <span className="text-gray-600 text-sm">{inclusion}</span>
                      </li>
                    ))}
                  </ul>
                </div>
                <div>
                  <h3 className="text-lg font-semibold text-red-600 mb-4 flex items-center">
                    <FiX className="w-5 h-5 mr-2" />
                    Not Included
                  </h3>
                  <ul className="space-y-2">
                    {tour.exclusions.map((exclusion, index) => (
                      <li key={index} className="flex items-start">
                        <FiX className="w-4 h-4 text-red-500 mr-2 mt-0.5 flex-shrink-0" />
                        <span className="text-gray-600 text-sm">{exclusion}</span>
                      </li>
                    ))}
                  </ul>
                </div>
              </div>
            </div>

            {/* Map */}
            {tour.location.coordinates && (
              <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
                <h2 className="text-2xl font-bold text-gray-900 mb-4">Location</h2>
                <div className="h-64 bg-gray-200 rounded-lg flex items-center justify-center">
                  <p className="text-gray-500">Interactive map would be embedded here</p>
                  {/* In a real implementation, you would embed Google Maps or another map service */}
                </div>
                <div className="mt-4 text-sm text-gray-600">
                  <FiMapPin className="w-4 h-4 inline mr-1" />
                  {tour.location.address}, {tour.location.city}, {tour.location.country}
                </div>
              </div>
            )}
          </div>

          {/* Booking Widget */}
          <div className="lg:col-span-1">
            <div className="sticky top-4">
              <div className="bg-white rounded-lg shadow-lg p-6">
                <div className="text-center mb-6">
                  <div className="text-3xl font-bold text-primary-600">
                    ${tour.pricing.basePrice}
                  </div>
                  <div className="text-sm text-gray-500">
                    {tour.pricing.priceType === 'per_person' ? 'per person' : 'per group'}
                  </div>
                </div>

                <div className="space-y-4 mb-6">
                  <div className="flex items-center justify-between text-sm">
                    <span className="text-gray-600">Duration:</span>
                    <span className="font-medium">{tour.duration.days}D/{tour.duration.nights}N</span>
                  </div>
                  <div className="flex items-center justify-between text-sm">
                    <span className="text-gray-600">Group Size:</span>
                    <span className="font-medium">Max {tour.maxGroupSize}</span>
                  </div>
                  <div className="flex items-center justify-between text-sm">
                    <span className="text-gray-600">Minimum Age:</span>
                    <span className="font-medium">{tour.minAge}+ years</span>
                  </div>
                </div>

                <Link
                  to={`/booking/${tour._id}`}
                  className="w-full btn-primary mb-4 flex items-center justify-center"
                >
                  <FiCalendar className="w-4 h-4 mr-2" />
                  Book Now
                </Link>

                <button className="w-full btn-secondary">
                  Check Availability
                </button>

                <div className="mt-6 pt-6 border-t border-gray-200">
                  <div className="flex items-center justify-center text-sm text-gray-500">
                    <FiCheck className="w-4 h-4 mr-1 text-green-500" />
                    Free cancellation up to 24 hours before
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TourDetail;

import React, { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Link } from 'react-router-dom';
import { toursAPI } from '../utils/api';
import { Tour, TourFilters, ToursResponse } from '../types';
import { Icon, Icons } from '../components/common/Icons';

const Tours: React.FC = () => {
  const [filters, setFilters] = useState<TourFilters>({
    page: 1,
    limit: 12,
    sortBy: 'createdAt',
    sortOrder: 'desc',
  });
  const [viewMode, setViewMode] = useState<'grid' | 'list'>('grid');
  const [showFilters, setShowFilters] = useState(false);

  const { data: toursData, isLoading, error } = useQuery({
    queryKey: ['tours', filters],
    queryFn: () => toursAPI.getTours(filters),
  });

  const categories = [
    { value: '', label: 'All Categories' },
    { value: 'adventure', label: 'Adventure' },
    { value: 'luxury', label: 'Luxury' },
    { value: 'family', label: 'Family' },
    { value: 'cultural', label: 'Cultural' },
    { value: 'beach', label: 'Beach' },
    { value: 'city', label: 'City' },
    { value: 'nature', label: 'Nature' },
  ];

  const durations = [
    { value: '', label: 'Any Duration' },
    { value: '1', label: '1 Day' },
    { value: '2', label: '2-3 Days' },
    { value: '4', label: '4-7 Days' },
    { value: '8', label: '8+ Days' },
  ];

  const sortOptions = [
    { value: 'createdAt-desc', label: 'Newest First' },
    { value: 'pricing.basePrice-asc', label: 'Price: Low to High' },
    { value: 'pricing.basePrice-desc', label: 'Price: High to Low' },
    { value: 'ratings.average-desc', label: 'Highest Rated' },
    { value: 'duration.days-asc', label: 'Duration: Short to Long' },
  ];

  const handleFilterChange = (key: keyof TourFilters, value: string | number | undefined) => {
    setFilters(prev => ({
      ...prev,
      [key]: value,
      page: 1, // Reset to first page when filters change
    }));
  };

  const handleSortChange = (sortValue: string) => {
    const [sortBy, sortOrder] = sortValue.split('-');
    setFilters(prev => ({
      ...prev,
      sortBy,
      sortOrder: sortOrder as 'asc' | 'desc',
      page: 1,
    }));
  };

  const handlePageChange = (page: number) => {
    setFilters(prev => ({ ...prev, page }));
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const clearFilters = () => {
    setFilters({
      page: 1,
      limit: 12,
      sortBy: 'createdAt',
      sortOrder: 'desc',
    });
  };

  const pagination = toursData?.data.pagination;
  const tours = toursData?.data.data || [];

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Section */}
      <div className="bg-primary-600 text-white py-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <h1 className="text-4xl md:text-5xl font-bold mb-4">Discover Amazing Tours</h1>
            <p className="text-xl text-blue-100 max-w-2xl mx-auto">
              Explore our curated collection of unforgettable travel experiences around the world
            </p>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex flex-col lg:flex-row gap-8">
          {/* Filters Sidebar */}
          <div className={`lg:w-64 ${showFilters ? 'block' : 'hidden lg:block'}`}>
            <div className="bg-white rounded-lg shadow-lg p-6 sticky top-4">
              <div className="flex items-center justify-between mb-6">
                <h3 className="text-lg font-semibold text-gray-900">Filters</h3>
                <button
                  onClick={clearFilters}
                  className="text-sm text-primary-600 hover:text-primary-700"
                >
                  Clear All
                </button>
              </div>

              {/* Search */}
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Search Tours
                </label>
                <div className="relative">
                  <Icon icon={Icons.FiSearch} className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
                  <input
                    type="text"
                    placeholder="Search destinations, tours..."
                    className="input-field pl-10"
                    value={filters.search || ''}
                    onChange={(e) => handleFilterChange('search', e.target.value)}
                  />
                </div>
              </div>

              {/* Category Filter */}
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Category
                </label>
                <select
                  className="input-field"
                  value={filters.category || ''}
                  onChange={(e) => handleFilterChange('category', e.target.value)}
                >
                  {categories.map(category => (
                    <option key={category.value} value={category.value}>
                      {category.label}
                    </option>
                  ))}
                </select>
              </div>

              {/* Price Range */}
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Price Range (USD)
                </label>
                <div className="grid grid-cols-2 gap-2">
                  <input
                    type="number"
                    placeholder="Min"
                    className="input-field"
                    value={filters.minPrice || ''}
                    onChange={(e) => handleFilterChange('minPrice', e.target.value ? Number(e.target.value) : undefined)}
                  />
                  <input
                    type="number"
                    placeholder="Max"
                    className="input-field"
                    value={filters.maxPrice || ''}
                    onChange={(e) => handleFilterChange('maxPrice', e.target.value ? Number(e.target.value) : undefined)}
                  />
                </div>
              </div>

              {/* Duration Filter */}
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Duration
                </label>
                <select
                  className="input-field"
                  value={filters.duration || ''}
                  onChange={(e) => handleFilterChange('duration', e.target.value)}
                >
                  {durations.map(duration => (
                    <option key={duration.value} value={duration.value}>
                      {duration.label}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          </div>

          {/* Main Content */}
          <div className="flex-1">
            {/* Top Bar */}
            <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-6 gap-4">
              <div className="flex items-center gap-4">
                <button
                  onClick={() => setShowFilters(!showFilters)}
                  className="lg:hidden btn-secondary flex items-center"
                >
                  <Icon icon={Icons.FiFilter} className="w-4 h-4 mr-2" />
                  Filters
                </button>
                {pagination && (
                  <p className="text-gray-600">
                    Showing {((pagination.currentPage - 1) * 12) + 1}-{Math.min(pagination.currentPage * 12, pagination.totalTours || pagination.totalItems)} of {pagination.totalTours || pagination.totalItems} tours
                  </p>
                )}
              </div>

              <div className="flex items-center gap-4">
                {/* Sort */}
                <select
                  className="input-field min-w-48"
                  value={`${filters.sortBy}-${filters.sortOrder}`}
                  onChange={(e) => handleSortChange(e.target.value)}
                >
                  {sortOptions.map(option => (
                    <option key={option.value} value={option.value}>
                      {option.label}
                    </option>
                  ))}
                </select>

                {/* View Mode */}
                <div className="flex border border-gray-300 rounded-lg">
                  <button
                    onClick={() => setViewMode('grid')}
                    className={`p-2 ${viewMode === 'grid' ? 'bg-primary-600 text-white' : 'text-gray-400'}`}
                  >
                    <Icon icon={Icons.FiGrid} className="w-5 h-5" />
                  </button>
                  <button
                    onClick={() => setViewMode('list')}
                    className={`p-2 ${viewMode === 'list' ? 'bg-primary-600 text-white' : 'text-gray-400'}`}
                  >
                    <Icon icon={Icons.FiList} className="w-5 h-5" />
                  </button>
                </div>
              </div>
            </div>

            {/* Tours Grid/List */}
            {isLoading ? (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {[...Array(6)].map((_, index) => (
                  <div key={index} className="card animate-pulse">
                    <div className="h-48 bg-gray-300 rounded-t-xl"></div>
                    <div className="p-6">
                      <div className="h-4 bg-gray-300 rounded mb-2"></div>
                      <div className="h-6 bg-gray-300 rounded mb-4"></div>
                      <div className="h-4 bg-gray-300 rounded"></div>
                    </div>
                  </div>
                ))}
              </div>
            ) : error ? (
              <div className="text-center py-12">
                <p className="text-gray-500">Error loading tours. Please try again.</p>
              </div>
            ) : !tours.length ? (
              <div className="text-center py-12">
                <p className="text-gray-500">No tours found matching your criteria.</p>
                <button
                  onClick={clearFilters}
                  className="mt-4 btn-primary"
                >
                  Clear Filters
                </button>
              </div>
            ) : (
              <>
                <div className={viewMode === 'grid'
                  ? 'grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6'
                  : 'space-y-6'
                }>
                  {tours.map((tour: Tour) => (
                    <TourCard key={tour._id} tour={tour} viewMode={viewMode} />
                  ))}
                </div>

                {/* Pagination */}
                {pagination && pagination.totalPages > 1 && (
                  <div className="flex justify-center mt-12">
                    <nav className="flex items-center space-x-2">
                      <button
                        onClick={() => handlePageChange(pagination.currentPage - 1)}
                        disabled={!pagination.hasPrev}
                        className="px-3 py-2 text-sm font-medium text-gray-500 bg-white border border-gray-300 rounded-md hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                      >
                        Previous
                      </button>

                      {[...Array(Math.min(5, pagination.totalPages))].map((_, index) => {
                        const page = index + 1;
                        return (
                          <button
                            key={page}
                            onClick={() => handlePageChange(page)}
                            className={`px-3 py-2 text-sm font-medium rounded-md ${
                              page === pagination.currentPage
                                ? 'bg-primary-600 text-white'
                                : 'text-gray-500 bg-white border border-gray-300 hover:bg-gray-50'
                            }`}
                          >
                            {page}
                          </button>
                        );
                      })}

                      <button
                        onClick={() => handlePageChange(pagination.currentPage + 1)}
                        disabled={!pagination.hasNext}
                        className="px-3 py-2 text-sm font-medium text-gray-500 bg-white border border-gray-300 rounded-md hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                      >
                        Next
                      </button>
                    </nav>
                  </div>
                )}
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

// Tour Card Component
interface TourCardProps {
  tour: Tour;
  viewMode: 'grid' | 'list';
}

const TourCard: React.FC<TourCardProps> = ({ tour, viewMode }) => {
  if (viewMode === 'list') {
    return (
      <div className="card overflow-hidden">
        <div className="flex flex-col md:flex-row">
          <div className="md:w-1/3">
            <img
              src={tour.images[0]?.url || `https://images.unsplash.com/photo-1469474968028-56623f02e42e?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80`}
              alt={tour.title}
              className="w-full h-48 md:h-full object-cover"
            />
          </div>
          <div className="flex-1 p-6">
            <div className="flex items-start justify-between">
              <div className="flex-1">
                <div className="flex items-center text-sm text-gray-500 mb-2">
                  <Icon icon={Icons.FiMapPin} className="w-4 h-4 mr-1" />
                  {tour.destination}
                </div>
                <h3 className="text-xl font-semibold text-gray-900 mb-2">
                  {tour.title}
                </h3>
                <p className="text-gray-600 mb-4 line-clamp-2">
                  {tour.shortDescription}
                </p>
                <div className="flex items-center space-x-4 text-sm text-gray-500">
                  <div className="flex items-center">
                    <Icon icon={Icons.FiClock} className="w-4 h-4 mr-1" />
                    {tour.duration.days}D/{tour.duration.nights}N
                  </div>
                  <div className="flex items-center">
                    <Icon icon={Icons.FiUsers} className="w-4 h-4 mr-1" />
                    Max {tour.maxGroupSize}
                  </div>
                  <div className="flex items-center">
                    <Icon icon={Icons.FiStar} className="w-4 h-4 text-yellow-400 mr-1" />
                    {tour.ratings.average.toFixed(1)} ({tour.ratings.count})
                  </div>
                </div>
              </div>
              <div className="text-right ml-6">
                <div className="text-2xl font-bold text-primary-600">
                  ${tour.pricing.basePrice}
                </div>
                <div className="text-sm text-gray-500 mb-4">per person</div>
                <Link
                  to={`/tours/${tour.slug}`}
                  className="btn-primary"
                >
                  View Details
                </Link>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="card overflow-hidden group">
      <div className="relative h-48 overflow-hidden">
        <img
          src={tour.images[0]?.url || `https://images.unsplash.com/photo-1469474968028-56623f02e42e?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80`}
          alt={tour.title}
          className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-300"
        />
        <div className="absolute top-4 right-4 bg-white rounded-full px-3 py-1 text-sm font-medium text-primary-600">
          ${tour.pricing.basePrice}
        </div>
      </div>
      <div className="p-6">
        <div className="flex items-center text-sm text-gray-500 mb-2">
          <Icon icon={Icons.FiMapPin} className="w-4 h-4 mr-1" />
          {tour.destination}
        </div>
        <h3 className="text-lg font-semibold text-gray-900 mb-2 line-clamp-2">
          {tour.title}
        </h3>
        <p className="text-gray-600 text-sm mb-4 line-clamp-2">
          {tour.shortDescription}
        </p>
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center">
            <Icon icon={Icons.FiClock} className="w-4 h-4 mr-1 text-gray-400" />
            <span className="text-sm text-gray-500">
              {tour.duration.days}D/{tour.duration.nights}N
            </span>
          </div>
          <div className="flex items-center">
            <Icon icon={Icons.FiStar} className="w-4 h-4 text-yellow-400 mr-1" />
            <span className="text-sm text-gray-500">
              {tour.ratings.average.toFixed(1)} ({tour.ratings.count})
            </span>
          </div>
        </div>
        <Link
          to={`/tours/${tour.slug}`}
          className="block w-full text-center btn-primary"
        >
          View Details
        </Link>
      </div>
    </div>
  );
};

export default Tours;

import React, { useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import { useAuth } from '../../contexts/AuthContext';
import { Icon, Icons } from '../../components/common/Icons';
import toast from 'react-hot-toast';

const schema = yup.object({
  email: yup.string().email('Invalid email').required('Email is required'),
  password: yup.string().min(6, 'Password must be at least 6 characters').required('Password is required'),
});

interface LoginFormData {
  email: string;
  password: string;
}

const Login: React.FC = () => {
  const [showPassword, setShowPassword] = useState(false);
  const { login, state } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  const from = location.state?.from?.pathname || '/';

  const {
    register,
    handleSubmit,
    formState: { errors, isSubmitting },
  } = useForm<LoginFormData>({
    resolver: yupResolver(schema),
  });

  const onSubmit = async (data: LoginFormData) => {
    try {
      await login(data.email, data.password);
      toast.success('Login successful!');
      navigate(from, { replace: true });
    } catch (error: any) {
      toast.error(error.message || 'Login failed');
    }
  };

  // Redirect if already authenticated
  React.useEffect(() => {
    if (state.isAuthenticated) {
      navigate(from, { replace: true });
    }
  }, [state.isAuthenticated, navigate, from]);

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col justify-center py-12 sm:px-6 lg:px-8">
      <div className="sm:mx-auto sm:w-full sm:max-w-md">
        <div className="text-center">
          <div className="mx-auto w-16 h-16 bg-primary-600 rounded-full flex items-center justify-center mb-4">
            <span className="text-white font-bold text-2xl">T</span>
          </div>
          <h2 className="text-3xl font-bold text-gray-900">Welcome back</h2>
          <p className="mt-2 text-gray-600">Sign in to your account to continue your journey</p>
        </div>
      </div>

      <div className="mt-8 sm:mx-auto sm:w-full sm:max-w-md">
        <div className="bg-white py-8 px-4 shadow-xl rounded-lg sm:px-10">
          <form className="space-y-6" onSubmit={handleSubmit(onSubmit)}>
            <div>
              <label htmlFor="email" className="block text-sm font-medium text-gray-700">
                Email address
              </label>
              <div className="mt-1 relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <Icon icon={Icons.FiMail} className="h-5 w-5 text-gray-400" />
                </div>
                <input
                  {...register('email')}
                  type="email"
                  autoComplete="email"
                  className={`input-field pl-10 ${errors.email ? 'border-red-500 focus:ring-red-500 focus:border-red-500' : ''}`}
                  placeholder="Enter your email"
                />
              </div>
              {errors.email && (
                <p className="mt-1 text-sm text-red-600">{errors.email.message}</p>
              )}
            </div>

            <div>
              <label htmlFor="password" className="block text-sm font-medium text-gray-700">
                Password
              </label>
              <div className="mt-1 relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <Icon icon={Icons.FiLock} className="h-5 w-5 text-gray-400" />
                </div>
                <input
                  {...register('password')}
                  type={showPassword ? 'text' : 'password'}
                  autoComplete="current-password"
                  className={`input-field pl-10 pr-10 ${errors.password ? 'border-red-500 focus:ring-red-500 focus:border-red-500' : ''}`}
                  placeholder="Enter your password"
                />
                <button
                  type="button"
                  className="absolute inset-y-0 right-0 pr-3 flex items-center"
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? (
                    <Icon icon={Icons.FiEyeOff} className="h-5 w-5 text-gray-400 hover:text-gray-500" />
                  ) : (
                    <Icon icon={Icons.FiEye} className="h-5 w-5 text-gray-400 hover:text-gray-500" />
                  )}
                </button>
              </div>
              {errors.password && (
                <p className="mt-1 text-sm text-red-600">{errors.password.message}</p>
              )}
            </div>

            <div className="flex items-center justify-between">
              <div className="flex items-center">
                <input
                  id="remember-me"
                  name="remember-me"
                  type="checkbox"
                  className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-gray-300 rounded"
                />
                <label htmlFor="remember-me" className="ml-2 block text-sm text-gray-900">
                  Remember me
                </label>
              </div>

              <div className="text-sm">
                <Link
                  to="/forgot-password"
                  className="font-medium text-primary-600 hover:text-primary-500"
                >
                  Forgot your password?
                </Link>
              </div>
            </div>

            <div>
              <button
                type="submit"
                disabled={isSubmitting}
                className="w-full btn-primary disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {isSubmitting ? (
                  <div className="flex items-center justify-center">
                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                    Signing in...
                  </div>
                ) : (
                  'Sign in'
                )}
              </button>
            </div>

            <div className="mt-6">
              <div className="relative">
                <div className="absolute inset-0 flex items-center">
                  <div className="w-full border-t border-gray-300" />
                </div>
                <div className="relative flex justify-center text-sm">
                  <span className="px-2 bg-white text-gray-500">New to TravelCo?</span>
                </div>
              </div>

              <div className="mt-6">
                <Link
                  to="/register"
                  className="w-full btn-secondary text-center block"
                >
                  Create an account
                </Link>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;

import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import { useAuth } from '../../contexts/AuthContext';
import { Icon, Icons } from '../../components/common/Icons';
import toast from 'react-hot-toast';

const schema = yup.object().shape({
  name: yup.string().min(2, 'Name must be at least 2 characters').required('Name is required'),
  email: yup.string().email('Invalid email').required('Email is required'),
  password: yup.string().min(6, 'Password must be at least 6 characters').required('Password is required'),
  confirmPassword: yup.string()
    .oneOf([yup.ref('password')], 'Passwords must match')
    .required('Please confirm your password'),
  phone: yup.string().optional(),
  country: yup.string().optional(),
  terms: yup.boolean().oneOf([true], 'You must accept the terms and conditions'),
});

interface RegisterFormData {
  name: string;
  email: string;
  password: string;
  confirmPassword: string;
  phone?: string;
  country?: string;
  terms: boolean;
}

const Register: React.FC = () => {
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const { register: registerUser, state } = useAuth();
  const navigate = useNavigate();

  const {
    register,
    handleSubmit,
    formState: { errors, isSubmitting },
  } = useForm<RegisterFormData>({
    resolver: yupResolver(schema),
  });

  const onSubmit = async (data: RegisterFormData) => {
    try {
      const { confirmPassword, terms, ...registerData } = data;
      await registerUser(registerData);
      toast.success('Registration successful! Welcome to TravelCo!');
      navigate('/');
    } catch (error: any) {
      toast.error(error.message || 'Registration failed');
    }
  };

  // Redirect if already authenticated
  React.useEffect(() => {
    if (state.isAuthenticated) {
      navigate('/');
    }
  }, [state.isAuthenticated, navigate]);

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col justify-center py-12 sm:px-6 lg:px-8">
      <div className="sm:mx-auto sm:w-full sm:max-w-md">
        <div className="text-center">
          <div className="mx-auto w-16 h-16 bg-primary-600 rounded-full flex items-center justify-center mb-4">
            <span className="text-white font-bold text-2xl">T</span>
          </div>
          <h2 className="text-3xl font-bold text-gray-900">Create your account</h2>
          <p className="mt-2 text-gray-600">Join thousands of travelers exploring the world</p>
        </div>
      </div>

      <div className="mt-8 sm:mx-auto sm:w-full sm:max-w-md">
        <div className="bg-white py-8 px-4 shadow-xl rounded-lg sm:px-10">
          <form className="space-y-6" onSubmit={handleSubmit(onSubmit)}>
            <div>
              <label htmlFor="name" className="block text-sm font-medium text-gray-700">
                Full name
              </label>
              <div className="mt-1 relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <Icon icon={Icons.FiUser} className="h-5 w-5 text-gray-400" />
                </div>
                <input
                  {...register('name')}
                  type="text"
                  autoComplete="name"
                  className={`input-field pl-10 ${errors.name ? 'border-red-500 focus:ring-red-500 focus:border-red-500' : ''}`}
                  placeholder="Enter your full name"
                />
              </div>
              {errors.name && (
                <p className="mt-1 text-sm text-red-600">{errors.name.message}</p>
              )}
            </div>

            <div>
              <label htmlFor="email" className="block text-sm font-medium text-gray-700">
                Email address
              </label>
              <div className="mt-1 relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <Icon icon={Icons.FiMail} className="h-5 w-5 text-gray-400" />
                </div>
                <input
                  {...register('email')}
                  type="email"
                  autoComplete="email"
                  className={`input-field pl-10 ${errors.email ? 'border-red-500 focus:ring-red-500 focus:border-red-500' : ''}`}
                  placeholder="Enter your email"
                />
              </div>
              {errors.email && (
                <p className="mt-1 text-sm text-red-600">{errors.email.message}</p>
              )}
            </div>

            <div className="grid grid-cols-1 gap-6 sm:grid-cols-2">
              <div>
                <label htmlFor="phone" className="block text-sm font-medium text-gray-700">
                  Phone (optional)
                </label>
                <div className="mt-1 relative">
                  <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    <Icon icon={Icons.FiPhone} className="h-5 w-5 text-gray-400" />
                  </div>
                  <input
                    {...register('phone')}
                    type="tel"
                    autoComplete="tel"
                    className="input-field pl-10"
                    placeholder="Phone number"
                  />
                </div>
              </div>

              <div>
                <label htmlFor="country" className="block text-sm font-medium text-gray-700">
                  Country (optional)
                </label>
                <div className="mt-1 relative">
                  <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    <Icon icon={Icons.FiMapPin} className="h-5 w-5 text-gray-400" />
                  </div>
                  <select
                    {...register('country')}
                    className="input-field pl-10"
                  >
                    <option value="">Select country</option>
                    <option value="Vietnam">Vietnam</option>
                    <option value="United States">United States</option>
                    <option value="Canada">Canada</option>
                    <option value="Australia">Australia</option>
                    <option value="United Kingdom">United Kingdom</option>
                    <option value="Germany">Germany</option>
                    <option value="France">France</option>
                    <option value="Japan">Japan</option>
                    <option value="South Korea">South Korea</option>
                    <option value="Singapore">Singapore</option>
                    <option value="Other">Other</option>
                  </select>
                </div>
              </div>
            </div>

            <div>
              <label htmlFor="password" className="block text-sm font-medium text-gray-700">
                Password
              </label>
              <div className="mt-1 relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <Icon icon={Icons.FiLock} className="h-5 w-5 text-gray-400" />
                </div>
                <input
                  {...register('password')}
                  type={showPassword ? 'text' : 'password'}
                  autoComplete="new-password"
                  className={`input-field pl-10 pr-10 ${errors.password ? 'border-red-500 focus:ring-red-500 focus:border-red-500' : ''}`}
                  placeholder="Create a password"
                />
                <button
                  type="button"
                  className="absolute inset-y-0 right-0 pr-3 flex items-center"
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? (
                    <Icon icon={Icons.FiEyeOff} className="h-5 w-5 text-gray-400 hover:text-gray-500" />
                  ) : (
                    <Icon icon={Icons.FiEye} className="h-5 w-5 text-gray-400 hover:text-gray-500" />
                  )}
                </button>
              </div>
              {errors.password && (
                <p className="mt-1 text-sm text-red-600">{errors.password.message}</p>
              )}
            </div>

            <div>
              <label htmlFor="confirmPassword" className="block text-sm font-medium text-gray-700">
                Confirm password
              </label>
              <div className="mt-1 relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <Icon icon={Icons.FiLock} className="h-5 w-5 text-gray-400" />
                </div>
                <input
                  {...register('confirmPassword')}
                  type={showConfirmPassword ? 'text' : 'password'}
                  autoComplete="new-password"
                  className={`input-field pl-10 pr-10 ${errors.confirmPassword ? 'border-red-500 focus:ring-red-500 focus:border-red-500' : ''}`}
                  placeholder="Confirm your password"
                />
                <button
                  type="button"
                  className="absolute inset-y-0 right-0 pr-3 flex items-center"
                  onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                >
                  {showConfirmPassword ? (
                    <Icon icon={Icons.FiEyeOff} className="h-5 w-5 text-gray-400 hover:text-gray-500" />
                  ) : (
                    <Icon icon={Icons.FiEye} className="h-5 w-5 text-gray-400 hover:text-gray-500" />
                  )}
                </button>
              </div>
              {errors.confirmPassword && (
                <p className="mt-1 text-sm text-red-600">{errors.confirmPassword.message}</p>
              )}
            </div>

            <div className="flex items-center">
              <input
                {...register('terms')}
                id="terms"
                type="checkbox"
                className={`h-4 w-4 text-primary-600 focus:ring-primary-500 border-gray-300 rounded ${errors.terms ? 'border-red-500' : ''}`}
              />
              <label htmlFor="terms" className="ml-2 block text-sm text-gray-900">
                I agree to the{' '}
                <Link to="/terms" className="text-primary-600 hover:text-primary-500">
                  Terms and Conditions
                </Link>{' '}
                and{' '}
                <Link to="/privacy" className="text-primary-600 hover:text-primary-500">
                  Privacy Policy
                </Link>
              </label>
            </div>
            {errors.terms && (
              <p className="mt-1 text-sm text-red-600">{errors.terms.message}</p>
            )}

            <div>
              <button
                type="submit"
                disabled={isSubmitting}
                className="w-full btn-primary disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {isSubmitting ? (
                  <div className="flex items-center justify-center">
                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                    Creating account...
                  </div>
                ) : (
                  'Create account'
                )}
              </button>
            </div>

            <div className="mt-6">
              <div className="relative">
                <div className="absolute inset-0 flex items-center">
                  <div className="w-full border-t border-gray-300" />
                </div>
                <div className="relative flex justify-center text-sm">
                  <span className="px-2 bg-white text-gray-500">Already have an account?</span>
                </div>
              </div>

              <div className="mt-6">
                <Link
                  to="/login"
                  className="w-full btn-secondary text-center block"
                >
                  Sign in instead
                </Link>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Register;

// User types
export interface User {
  _id: string;
  name: string;
  email: string;
  phone?: string;
  country?: string;
  role: 'customer' | 'admin' | 'editor';
  avatar?: string;
  isVerified: boolean;
  preferences: {
    language: 'en' | 'vi';
    currency: string;
    newsletter: boolean;
  };
  bookingHistory: string[];
  createdAt: string;
  updatedAt: string;
}

// Tour types
export interface Tour {
  _id: string;
  title: string;
  slug: string;
  description: string;
  shortDescription: string;
  destination: string;
  duration: {
    days: number;
    nights: number;
  };
  pricing: {
    basePrice: number;
    currency: string;
    priceType: 'per_person' | 'per_group';
    seasonalPricing: SeasonalPricing[];
    groupDiscounts: GroupDiscount[];
  };
  itinerary: ItineraryDay[];
  inclusions: string[];
  exclusions: string[];
  highlights: string[];
  images: TourImage[];
  videos: TourVideo[];
  category: 'adventure' | 'luxury' | 'family' | 'cultural' | 'beach' | 'city' | 'nature';
  difficulty: 'easy' | 'moderate' | 'challenging' | 'extreme';
  maxGroupSize: number;
  minAge: number;
  availability: TourAvailability[];
  location: {
    coordinates: [number, number];
    address: string;
    city: string;
    country: string;
  };
  status: 'draft' | 'published' | 'archived';
  featured: boolean;
  ratings: {
    average: number;
    count: number;
  };
  tags: string[];
  seoData: {
    metaTitle: string;
    metaDescription: string;
    keywords: string[];
  };
  createdAt: string;
  updatedAt: string;
}

export interface SeasonalPricing {
  season: string;
  startDate: string;
  endDate: string;
  multiplier: number;
}

export interface GroupDiscount {
  minPeople: number;
  discount: number;
}

export interface ItineraryDay {
  day: number;
  title: string;
  description: string;
  activities: string[];
  meals: string[];
  accommodation: string;
}

export interface TourImage {
  url: string;
  alt: string;
  isPrimary: boolean;
}

export interface TourVideo {
  url: string;
  title: string;
}

export interface TourAvailability {
  startDate: string;
  endDate: string;
  availableSpots: number;
  bookedSpots: number;
}

// Booking types
export interface Booking {
  _id: string;
  bookingNumber: string;
  tour: Tour;
  customer: User;
  travelers: Traveler[];
  bookingDetails: {
    startDate: string;
    endDate: string;
    numberOfTravelers: {
      adults: number;
      children: number;
      infants: number;
    };
    totalTravelers: number;
  };
  pricing: {
    basePrice: number;
    seasonalMultiplier?: number;
    groupDiscount?: number;
    addOns: AddOn[];
    subtotal: number;
    taxes: number;
    totalAmount: number;
    currency: string;
  };
  payment: {
    status: 'pending' | 'paid' | 'partially_paid' | 'refunded' | 'failed';
    method?: 'credit_card' | 'bank_transfer' | 'vnpay' | 'momo';
    transactionId?: string;
    paidAmount: number;
    paymentDate?: string;
    refundAmount: number;
    refundDate?: string;
  };
  status: 'pending' | 'confirmed' | 'in_progress' | 'completed' | 'cancelled';
  specialRequests?: string;
  emergencyContact?: {
    name: string;
    phone: string;
    relationship: string;
  };
  documents: BookingDocument[];
  notifications: {
    confirmationSent: boolean;
    reminderSent: boolean;
    followUpSent: boolean;
  };
  createdBy?: string;
  notes: BookingNote[];
  createdAt: string;
  updatedAt: string;
}

export interface Traveler {
  name: string;
  age?: number;
  type: 'adult' | 'child' | 'infant';
  passportNumber?: string;
  nationality?: string;
  dietaryRequirements?: string;
}

export interface AddOn {
  name: string;
  price: number;
  quantity: number;
}

export interface BookingDocument {
  type: string;
  url: string;
  uploadDate: string;
}

export interface BookingNote {
  content: string;
  author: string;
  createdAt: string;
}

// Review types
export interface Review {
  _id: string;
  tour: string | Tour;
  customer: string | User;
  booking: string | Booking;
  rating: number;
  title: string;
  content: string;
  aspects: {
    guide?: number;
    accommodation?: number;
    transport?: number;
    value?: number;
  };
  photos: string[];
  status: 'pending' | 'approved' | 'rejected';
  featured: boolean;
  helpful: number;
  createdAt: string;
  updatedAt: string;
}

// Content types
export interface Content {
  _id: string;
  type: 'page' | 'blog' | 'destination' | 'faq';
  title: string;
  slug: string;
  content: string;
  excerpt?: string;
  featuredImage?: string;
  gallery: string[];
  author: string | User;
  status: 'draft' | 'published' | 'archived';
  featured: boolean;
  categories: string[];
  tags: string[];
  language: 'en' | 'vi';
  seoData: {
    metaTitle: string;
    metaDescription: string;
    keywords: string[];
  };
  views: number;
  createdAt: string;
  updatedAt: string;
}

// API Response types
export interface ApiResponse<T> {
  data?: T;
  message: string;
  success: boolean;
}

export interface PaginationResponse<T> {
  data: T[];
  pagination: {
    currentPage: number;
    totalPages: number;
    totalItems: number;
    totalTours?: number;
    totalBookings?: number;
    hasNext: boolean;
    hasPrev: boolean;
  };
}

// Fixed API response for tours specifically
export interface ToursResponse {
  tours: Tour[];
  pagination: {
    currentPage: number;
    totalPages: number;
    totalTours: number;
    hasNext: boolean;
    hasPrev: boolean;
  };
}

// Form types
export interface LoginForm {
  email: string;
  password: string;
}

export interface RegisterForm {
  name: string;
  email: string;
  password: string;
  confirmPassword: string;
  phone?: string;
  country?: string;
}

export interface BookingForm {
  tourId: string;
  startDate: string;
  numberOfTravelers: {
    adults: number;
    children: number;
    infants: number;
  };
  travelers: Traveler[];
  specialRequests?: string;
  emergencyContact?: {
    name: string;
    phone: string;
    relationship: string;
  };
}

export interface ContactForm {
  name: string;
  email: string;
  phone?: string;
  subject: string;
  message: string;
}

// Filter types
export interface TourFilters {
  destination?: string;
  category?: string;
  minPrice?: number;
  maxPrice?: number;
  duration?: number;
  sortBy?: string;
  sortOrder?: 'asc' | 'desc';
  search?: string;
  page?: number;
  limit?: number;
}

import axios, { AxiosResponse } from 'axios';
import {
  User,
  Tour,
  Booking,
  Review,
  Content,
  LoginForm,
  RegisterForm,
  BookingForm,
  TourFilters,
  ApiResponse,
  PaginationResponse
} from '../types';

// Create axios instance
const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:5000/api',
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor to add auth token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor to handle errors
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// Auth API
export const authAPI = {
  login: async (data: LoginForm): Promise<AxiosResponse<{ token: string; user: User }>> => {
    return api.post('/auth/login', data);
  },

  register: async (data: RegisterForm): Promise<AxiosResponse<{ token: string; user: User }>> => {
    return api.post('/auth/register', data);
  },

  getProfile: async (): Promise<AxiosResponse<{ user: User }>> => {
    return api.get('/auth/profile');
  },

  updateProfile: async (data: Partial<User>): Promise<AxiosResponse<{ user: User }>> => {
    return api.put('/auth/profile', data);
  },

  changePassword: async (data: { currentPassword: string; newPassword: string }): Promise<AxiosResponse<ApiResponse<any>>> => {
    return api.put('/auth/change-password', data);
  },
};

// Tours API
export const toursAPI = {
  getTours: async (filters?: TourFilters): Promise<AxiosResponse<PaginationResponse<Tour>>> => {
    const params = new URLSearchParams();
    if (filters) {
      Object.entries(filters).forEach(([key, value]) => {
        if (value !== undefined && value !== null) {
          params.append(key, value.toString());
        }
      });
    }
    return api.get(`/tours?${params.toString()}`);
  },

  getFeaturedTours: async (): Promise<AxiosResponse<{ tours: Tour[] }>> => {
    return api.get('/tours/featured');
  },

  getTourBySlug: async (slug: string): Promise<AxiosResponse<{ tour: Tour }>> => {
    return api.get(`/tours/${slug}`);
  },

  checkAvailability: async (tourId: string, startDate: string, groupSize: number): Promise<AxiosResponse<{ available: boolean; price: number; currency: string }>> => {
    return api.get(`/tours/${tourId}/availability?startDate=${startDate}&groupSize=${groupSize}`);
  },

  createTour: async (data: FormData): Promise<AxiosResponse<{ tour: Tour }>> => {
    return api.post('/tours', data, {
      headers: { 'Content-Type': 'multipart/form-data' },
    });
  },

  updateTour: async (id: string, data: Partial<Tour>): Promise<AxiosResponse<{ tour: Tour }>> => {
    return api.put(`/tours/${id}`, data);
  },

  deleteTour: async (id: string): Promise<AxiosResponse<ApiResponse<any>>> => {
    return api.delete(`/tours/${id}`);
  },
};

// Bookings API
export const bookingsAPI = {
  createBooking: async (data: BookingForm): Promise<AxiosResponse<{ booking: Booking }>> => {
    return api.post('/bookings', data);
  },

  getUserBookings: async (page = 1, status?: string): Promise<AxiosResponse<PaginationResponse<Booking>>> => {
    const params = new URLSearchParams({ page: page.toString() });
    if (status) params.append('status', status);
    return api.get(`/bookings?${params.toString()}`);
  },

  getBooking: async (id: string): Promise<AxiosResponse<{ booking: Booking }>> => {
    return api.get(`/bookings/${id}`);
  },

  cancelBooking: async (id: string, reason: string): Promise<AxiosResponse<{ booking: Booking }>> => {
    return api.put(`/bookings/${id}/cancel`, { reason });
  },
};

// Payments API
export const paymentsAPI = {
  createPaymentIntent: async (bookingId: string): Promise<AxiosResponse<{ clientSecret: string; amount: number; currency: string }>> => {
    return api.post('/payments/create-intent', { bookingId });
  },

  confirmPayment: async (data: { bookingId: string; paymentIntentId: string; paymentMethod: string }): Promise<AxiosResponse<{ booking: Booking }>> => {
    return api.post('/payments/confirm', data);
  },

  createVNPayPayment: async (bookingId: string): Promise<AxiosResponse<{ paymentUrl: string; vnpayData: any }>> => {
    return api.post('/payments/vnpay', { bookingId });
  },
};

// Reviews API
export const reviewsAPI = {
  getReviews: async (tourId?: string, page = 1): Promise<AxiosResponse<PaginationResponse<Review>>> => {
    const params = new URLSearchParams({ page: page.toString() });
    if (tourId) params.append('tourId', tourId);
    return api.get(`/reviews?${params.toString()}`);
  },

  createReview: async (data: Partial<Review>): Promise<AxiosResponse<{ review: Review }>> => {
    return api.post('/reviews', data);
  },

  updateReview: async (id: string, data: Partial<Review>): Promise<AxiosResponse<{ review: Review }>> => {
    return api.put(`/reviews/${id}`, data);
  },

  deleteReview: async (id: string): Promise<AxiosResponse<ApiResponse<any>>> => {
    return api.delete(`/reviews/${id}`);
  },
};

